package programs;

import java.util.Scanner;

public class PosorNeg {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Scanner sc = new Scanner(System.in);
		int num = sc.nextInt();

		if (num > 0) {
			System.out.println("positive");
		} else
			System.out.println("negative");

	}

}
